import React, { useState, Fragment } from 'react'
import styled from 'styled-components';

export default function AddRecipe(props) {

    const [state, setState] = useState({
        image: "",
        video: "",
        title: "",
        ingredients: [{ingred_detail: ''}],
        recipe_steps: [{step_detail: ''}]
    })

    const displayFormHandler = () => {
        props.setRecipeForm((prevState) => {
          return !prevState;
        });
    };

    const changeHandler = (e) => {
        e.persist();
        props.setFormState((prevState) => ({
          ...prevState,
          [e.target.name]: e.target.value,
        }));
        console.log(props.formState);
    };

    const dynamicFormChangeHandler = (index, e) => {
        if (e.target.name === "ingredient") {
            state.ingredients[index].ingred_detail = e.target.value
        } else if (e.target.name === "recipe_step") {
            state.recipe_steps[index].recipe_step = e.target.value
        }

        props.setFormState(state)
    }

    const changeHandlerFiles = (e) => {
        e.persist();
        props.setFormState((prevState) => ({
          ...prevState,
          [e.target.name]: e.target.files[0],
        }));
        console.log(props.formState);
    };

    const handleAddIngredientFields = () => {
        state.ingredients.push({ingred_detail: ''})
        props.setFormState(state)
        console.log(state)
    }

    const handleRemoveIngredientFields = (index) => {
        state.ingredients.splice(index, 1)
        props.setFormState(state)
        console.log(state)
    }

    const handleRecipeCreation = (e) => {
        e.preventDefault();
        console.log("form state", props.formState)
        // const token = localStorage.getItem("token")

        // const form = new FormData()
        // form.append("image", props.formState.image)
        // form.append("video", props.formState.video)
        // form.append("title", props.formState.title)
        // form.append("ingred_list", props.formState.ingred_list)
        // form.append("description", props.formState.description)

        // fetch("http://localhost:3000/recipes", {
        //     method: "POST",
        //     headers: {
        //         "Authorization": `Bearer ${token}`,
        //     },
        //     body: form,
        // })
        //     .then((r) => r.json())
        //     .then((newRecipe) => {
        //         console.log(newRecipe)
        //         props.renderNewRecipe((prevState => [newRecipe, ...prevState]))
        //     });
        // props.setFormState({image: "", video: "", title: "", ingred_list: "", description: ""});
        // props.setRecipeForm(false);
    };

    return (
        <>
            {props.recipeForm ? (
                <div className="form">
                    <h1>New Recipe</h1>
                    <form onSubmit={handleRecipeCreation}>
                        <label>Image Upload:</label>
                        <input type="file" name="image" onChange={changeHandlerFiles}/>
                        <br/>
                        <label>Video Upload</label>
                        <input type="file" name="video" onChange={changeHandlerFiles}/>
                        <br/>
                        <label>Title:</label>
                        <input type="text" name="title" onChange={changeHandler}/>
                        <br/>
                        <label>Ingredient List:</label>
                        {state.ingredients.map((ingredient, index) => (
                            <Fragment key={`${ingredient}~${index}`}>
                                <div>
                                    <label htmlFor="ingredient">Ingredient</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="ingred_detail"
                                        name="ingred_detail"
                                        value={ingredient.ingred_detail}
                                        onChange={e => dynamicFormChangeHandler(index, e)}
                                    />
                                </div>
                                <div>
                                    <button
                                        type="button"
                                        onClick={() => handleRemoveIngredientFields(index)}
                                    >
                                        -
                                    </button>
                                    <button
                                        type="button"
                                        onClick={() => handleAddIngredientFields()}
                                    >
                                        +
                                    </button>
                                </div>
                            </Fragment>
                        ))}
                        {/* <input type="textarea" name="ingred_list" onChange={changeHandler}/> */}
                        <br/>
                        <label>Directions:</label>
                        {state.recipe_steps.map((recipe_step, index) => (
                            <Fragment key={`${recipe_step}~${index}`}>
                                <div>
                                    <label htmlFor="recipe_step">Step</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="recipe_step"
                                        name="recipe_step"
                                        value={recipe_step.step_detail}
                                        onChange={e => dynamicFormChangeHandler(index, e)}
                                    />
                                </div>
                                <div>
                                    <button
                                        type="button"
                                    >
                                        -
                                    </button>
                                    <button
                                        type="button"
                                    >
                                        +
                                    </button>
                                </div>
                            </Fragment>
                        ))}
                        {/* <input type="textarea" name="description" onChange={changeHandler}/> */}
                        <br/>
                        <input type="submit"/>
                    </form>
                    <button
                        type="button"
                        onClick={displayFormHandler}
                        className=""
                    >
                        Cancel
                </button>
                </div>
                ) : (
                <button
                    onClick={displayFormHandler}
                    className="add-recipe-button"
                >
                    +
                </button>
            )}
        </>
    )
}
