import React, { useState, useEffect } from 'react'
import RecipeCard from './RecipeCard'
import AddRecipe from './AddRecipe'
// import { Link } from 'react-router-dom'
import './Recipes.css'

export default function RecipeContainer() {
    const [state, setState] = useState([]);
    const [recipeForm, setRecipeForm] = useState(false);
    const [recipeId, setRecipeId] = useState("");
    // const [modalOpen, setModalOpen] = useState(false);
    const [inputList, setInputList] = useState([
        {ingredient: ''}
    ])

    const [formState, setFormState] = useState({
        image: "",
        video: "",
        title: "",
        ingredients: [{ingredient: ''}],
        recipe_steps: [{recipe_step: ''}]
        // ingred_list: "",
        // description: "",
    });

    // const showEditForm = (recipeObj) => {
    //     setRecipeId(recipeObj.id);
    //     setRecipeForm(true);
    //     setFormState({
    //         image: recipeObj.image,
    //         video: recipeObj.video,
    //         title: recipeObj.title,
    //         ingred_list: recipeObj.ingred_list,
    //         description: recipeObj.description,
    //     });
    // }

    useEffect(() => {
        const token = localStorage.getItem("token")
        fetch('http://localhost:3000/recipes', {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`,
                "Accept": "application/json"
            }
        })
            .then(r => r.json())
            .then(recipesArray => {
                setState(recipesArray)
                console.log(recipesArray)
            });
    }, []);

    const renderRecipe = () => {
        return state.map(recipe => (
            <RecipeCard 
                key={recipe.id} 
                recipeInfo={recipe}
                // showForm={showEditForm}
                // recipeSetState={setState}
            />
        ))
    }

    return (
        <div className="recipes-container">
            <div id="spacer" />
            <img src=""></img>
            <p id="all-recipes-header">All Recipes</p>
            <section className="recipe-cards">
                {state.length > 0 && renderRecipe()}
            </section>
            <AddRecipe 
                recipeForm={recipeForm}
                renderNewRecipe={setState}
                recipeId={recipeId}
                formState={formState}
                setRecipeForm={setRecipeForm}
                setIsNew={setRecipeId}
                setFormState={setFormState}
            />
        </div>
    )
}
