import React, { useState, useEffect } from 'react'
import RatingExampleClearable from './AverageRating'
import CommentContainer from './CommentContainer'
import { useHistory } from 'react-router-dom'

export default function RecipeDetail(props) {
    console.log(props)
    const [state, setState] = useState({});
    const [comment, setComment] = useState({});
    const [recipeForm, setRecipeForm] = useState(false);
    const [formState, setFormState] = useState({
      image: "",
      video: "",
      title: "",
      ingred_list: "",
      description: "",
    });

    const showEditForm = (recipeObj) => {
        setRecipeForm(true);
        setFormState({
            image: recipeObj.image,
            video: recipeObj.video,
            title: recipeObj.title,
            ingred_list: recipeObj.ingred_list,
            description: recipeObj.description,
        });
    }

    const displayFormHandler = () => {
      props.setRecipeForm((prevState) => {
        return !prevState;
      });
    };
    // console.log("recipe form state", recipeForm);
    // console.log("comm", comment);
    const user = props.user
    // console.log(user.id)
    console.log(state)
    const history = useHistory();

    useEffect(() => {
      const token = localStorage.getItem("token")
      fetch(`http://localhost:3000/recipes/${props.match.params.id}`, {
        method: "GET",
        headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
            "Accept": "application/json"
        }
      })
        .then((r) => r.json())
        .then((recipeObj) => {
          setState(recipeObj);
        });
    }, []);
    console.log(state)

    const handleDelete = () => {
      const token = localStorage.getItem("token")
      fetch(`http://localhost:3000/recipes/${props.match.params.id}`, {
        method: "DELETE",
        headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
            "Accept": "application/json"
        }
      })
        .then((r) => r.json())
        .then(deletedComment => {
          history.push('/recipes')
        })
    }

    const handleEdit = () => {

    }

    return (
        <div>
            <h4>{state.title}</h4>
            <br></br>
            <p>{state.ingred_list}</p>
            <br></br>
            <p>{state.description}</p>
            <br></br>
            <video controls width="250" src={state.video}></video>
            <br></br>
            {state.user_id === user.id && (
              <>
                <button onClick={showEditForm}>Edit Recipe</button>
                <button onClick={handleDelete}>Delete Recipe</button>
              </>
            )}
            <hr></hr>
            <RatingExampleClearable />
            <hr></hr>
            <CommentContainer user={props.user} recipe={state} comments={state.comments}/>
        </div>
    )
}
