import React, {useState, useEffect} from 'react';
import { Switch, Route, BrowserRouter, Link, useHistory } from 'react-router-dom'
import logo from './logo.svg';
import './App.css';
import RecipeContainer from './components/Recipes-Page/RecipeContainer'
import RecipeDetail from './components/Recipe-Detail-Page/RecipeDetail'
import Navbar from './components/Navbar/Navbar'
import Home from './components/Home-Page/Home'
import Login from './components/Login/Login'
import Signup from './components/Signup/Signup'
import NavbarLoggedOut from './components/Navbar/NavbarLoggedOut'

function App() {
  const [user, setUser] = useState({})
  const [form, setForm] = useState("")
  const history = useHistory();

  // useEffect(() => {
  //   const token = localStorage.getItem("token")
  //   if(token) {
  //     fetch(`http://localhost:3000/auto_login`, {
  //       headers: {
  //         Authorization: `Bearer ${token}`
  //       }
  //     })
  //     .then(r => r.json())
  //     .then(data => {
  //       setUser(data)
  //       // console.log(data)
  //     })
  //   }
  // }, [])

  const handleLogin = (user) => {
    setUser(user)
    if (user) {
      history.push(`/home`);
    }
  }

  const handleNavSwitch = (input) => {
    setForm(input)
  }

  const handleAuthClick = () => {
    const token = localStorage.getItem("token")
    fetch(`http://localhost:3000/user_is_authed`, {
      headers: {
        "Authorization": `Bearer ${token}`
      }
    })
    .then(r => r.json())
    .then(data => console.log(data))
  }

  console.log(user)

  const renderForm = () => {
    switch(form) {
      case "login": 
        return <Login handleLogin={handleLogin} />
        break;
      default: 
        return <Signup handleLogin={handleLogin} />
    }
  }

  const handleLogout = () => {
    setUser({})
  }

  return (
      <div className="App">
        <BrowserRouter>
          <Navbar handleNavSwitch={handleNavSwitch} user={user} handleLogout={handleLogout}/>
          <Switch>
            {user !== {} ? (
              <>
                <Route exact path="/home" render={() => <Home />}/>
                <Route exact path="/recipes" render={() => <RecipeContainer />}/>
                <Route exact path="/recipes/:id" render={(props) => <RecipeDetail {...props} />}/>
              </>
            ) : (
              <>
                <Route exact path="/signup" render={() => <Signup handleLogin={handleLogin} />}/>
                <Route exact path="/login" render={() => <Login handleLogin={handleLogin}/>}/>
              </>
            )}
          </Switch>
        </BrowserRouter>
      </div>
  );
}

export default App;
