import React from 'react'

export default function Comment(props) {
    console.log(props)

    const handleDelete = () => {
        const token = localStorage.getItem("token")
        fetch(`http://localhost:3000/comments/${props.comment.id}`, {
          method: "DELETE",
          headers: {
              "Content-Type": "application/json",
              "Authorization": `Bearer ${token}`,
              "Accept": "application/json"
          }
        })
          .then((r) => r.json())
          .then(deletedComment => {
            // history.push('/recipes')
            console.log(deletedComment)
          })
    }

    return (
        <div className="comment">
            <p>@{props.comment.username}</p>
            <p>{props.comment.content}</p>
            {/* <p>{props.comment.created_at.split("T")[0]}</p> */}
            {props.user.id === props.comment.user_id && (
                <>
                <button className="buttons"><img src="https://res.cloudinary.com/hsk23/image/upload/v1597717386/Food%20Feed/edit-new-icon-22_if53kc.png" width="20%"/></button>
                <button onClick={handleDelete} className="buttons"><img src="https://res.cloudinary.com/hsk23/image/upload/v1597717386/Food%20Feed/3058-200_gkxsdp.png" width="20%"/></button>
              </>
            )}
            <hr/>
        </div>

    )
}
