import React from 'react'
import { Button, Form } from 'semantic-ui-react'

export default function AddComment(props) {
    // console.log(props.recipe)

    const changeHandler = (e) => {
        e.persist();
        props.setFormState((prevState) => ({
          ...prevState,
          [e.target.name]: e.target.value,
        }));
        console.log(props.formState);
    };

    const handleCommentCreation = (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token")

        fetch(`http://localhost:3000/recipes/${props.recipe.id}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`,
                "Accept": "application/json"
            }
            // body: JSON.stringify(newComment),
        })
            .then((r) => r.json())
            .then((newComment) => {
                console.log(newComment)
                props.renderNewComment((prevState => [newComment, ...prevState]))
            });
        props.setFormState({content: ""});
    };

        return (
            <div className="form">
                <Form onSubmit={handleCommentCreation}>
                    <Form.Field>
                    <label>New Comment</label>
                    <input placeholder='Add your comment here...' />
                    </Form.Field>
                    <Button type='submit'>Submit</Button>
                </Form>
            </div>
        )
    }
